select.execute:

var snRequest,
  snData;

if ( method === "post" ) {
  snRequest = y.rest( "https://simple-note.appspot.com/api" + path );
  snData = snRequest
    .timeout( 5000 )
    .accept( "text/plain, application/json" )
    .contentType( "application/x-www-form-urlencoded" )
    .post( data );
}        
else {
  snRequest = y.rest( "https://simple-note.appspot.com/api" + path + "?" + data );
  snData = snRequest
    .timeout( 5000 )
    .accept( "text/plain" )
    .contentType( "application/x-www-form-urlencoded" )
    .get();
}

response.object = {
  "response": ( path === "/search" ) ? y.xmlToJson( snData.response ) : snData.response,
  "headers": snData.headers,
  "status": snData.status
};
